import { Component, EventEmitter, Input, Output } from '@angular/core';
import { AccountService } from '../Services/account.service';

@Component({
  selector: 'app-account',
  templateUrl: './account.component.html',
  styleUrls: ['./account.component.css']
})
export class AccountComponent {
  @Input() account: {name: string, status: string} = {name : '', status : ''};
  @Input() id: number = 0;
  // @Output() statusChanged = new EventEmitter<{id: number, newStatus: string}>();

  constructor(private accountService : AccountService) {}

  onSetTo(status: string) {

    //this.statusChanged.emit({id: this.id, newStatus: status});
    //console.log('A server status changed, new status: ' + status);
  }
}
<div class="row">
  <div class="col-xs-12 col-md-8 col-md-offset-2">
    <h5>{{ account.name }}</h5>
    <hr>
    <p>This account is {{ account.status }}</p>
    <button class="btn btn-outline-success" (click)="onSetTo('active')">Set to 'active'</button>
    <button class="btn btn-outline-danger" (click)="onSetTo('inactive')">Set to 'inactive'</button>
    <button class="btn btn-outline-primary" (click)="onSetTo('unknown')">Set to 'unknown'</button>
  </div>
</div>
