import { Component, EventEmitter, Output } from '@angular/core'; 
import { LoggingService } from '../../Services/loging.service';
import { AccountService } from '../../Services/account.service'; 
@Component({ 
 selector: 'app-new-account', 
 templateUrl: './new-account.component.html', 
 styleUrls: ['./new-account.component.css'] 
}) 
export class NewAccountComponent { 
 @Output() accountAdded = new EventEmitter<{name: string, status: string}>(); 
 constructor(private service : LoggingService, private accountservice : AccountService){ 
 } 
 onCreateAccount(accountName: string, accountStatus: string) { 
 const newaccount = { 
 name: accountName, 
 status: accountStatus 
 }; 
 this.accountservice.onAccountAdded(newaccount); 
 this.accountservice.statusUpdated.emit(accountStatus); 
 } 
}
