import { Component, OnInit } from '@angular/core';
import { AccountService } from './Component/Services/account.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  accounts : any;

  constructor(private accountservice : AccountService) {
    this.accountservice.statusUpdated.subscribe(s => console.log('New Status:' + s));}

  ngOnInit() {
    this.accounts = this.accountservice.accounts;
  }

  onStatusChanged(e : Event) {
    //updateInfo : {id: number, newStatus: string} | null = null;
    //this.accountservice.onStatusChanged(updateInfo);
  }

  onAccountAdded(e : Event) {}
}
